package tw.tom.java.example;

public class Day2_ifelse_03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 10, b = 3;
		if (--a >= 10 && ++b <= 3) {
			System.out.println("OK: a = " + a + "; b = " + b);
		}else {
			System.out.println("XX: a = " + a + "; b = " + b);
		}

	}

}
