package tw.tom.java.example;

public class Day4_WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a =10;
		
		while(a>0) 
		{			
			System.out.println(a);
			a--;
		}
        a=10;
        for(;a>0;)
        {
        	System.out.println(a);
			a--;
        }
		
	}

}
